import React, { ReactNode, useState } from 'react'

import Header from './header'
import Footer from './footer'
import Sidebar from './sidebar'

import CheckboxContext from '../contexts/CheckboxContext'

type Props = {
  children: ReactNode;
  isIdolslist?: boolean;
}

const Layout = ({ children, isIdolslist }: Props) => {

  // const [check, setCheck] = useState(false);
  // const [name, setName] = useState('');

  // const [checkList, setCheckList] = useState<boolean[]>([]);
  const [groupidList,setGroupidList]=useState<string[]>(["1","2","3"]);
  const [checkList, setCheckList] = useState<boolean[]>(new Array(3).fill(true));

    return (
      // <CheckboxContext.Provider value={{check, setCheck, name, setName}}>
      <CheckboxContext.Provider value={{ checkList, setCheckList, groupidList, setGroupidList }}>
        <div className="App flex flex-col">
          <Header />
          <main className="py-5 px-1 md:p-6">
            <div className="flex">
              <div className="basis-1/12 p-4 hidden md:inline-block">
                <Sidebar isIdolslist={isIdolslist}/>
              </div>
              <div className="flex-auto md:p-4">
                 {children}
              </div>
            </div>
          </main>
          <Footer />
        </div>
      </CheckboxContext.Provider>

    )
  }

  export default Layout
