import React, {} from "preact/compat";

import Checkbox from "../atom/checkbox";
import CheckboxContext from "../../contexts/CheckboxContext";


const IdolsList:React.FC = () => {

    const { groupIds, setGroupIds } = useContext(CheckboxContext);

    const idols = [
        { name: "yosugala", color: "accent-purple-500"},
        { name: "きのホ。", color: "accent-emerald-600"},
        { name: "SANDAL TELEPHONE", color: "accent-rose-500"}
      ];

    return(

        <div className="mt-16">
            <h2 className="ml-1 text-lg">アイドル一覧</h2>
                <div className="flex flex-col">           
                    {/* {idols.map((idol, index) => (
                    <Checkbox key={index} name={idol.name} isCheck={true} color={idol.color}/>
                    ))} */}
                     <div>
                        <Checkbox name="グループ1" isCheck={true} color="bg-red-500" onChange={() => setGroupIds([...groupIds, "グループ1"])}/>
                        <Checkbox name="グループ2" isCheck={false} color="bg-green-500" onChange={() => setGroupIds([...groupIds, "グループ2"])}/>
                    </div>
                </div>
        </div>

    )


}

export default IdolsList
