import React, { ReactNode, useState } from 'react'

import Header from './header'
import Footer from './footer'
import Sidebar from './sidebar'

import CheckboxContext from '../contexts/CheckboxContext'

type Props = {
  children: ReactNode;
  isIdolslist?: boolean;
}

const Layout = ({ children, isIdolslist }: Props) => {

  // const [check, setCheck] = useState(false);
  // const [name, setName] = useState('');

  const [checkList, setCheckList] = useState<string[]>([]);
  const [nameList, setNameList] = useState<string[]>([]);

    return (
      // <CheckboxContext.Provider value={{check, setCheck, name, setName}}>
      <CheckboxContext.Provider value={{ checkList, setCheckList, nameList, setName }}>
        <div className="App flex flex-col">
          <Header />
          <main className="p-6">
            <div className="flex">
              <div className="basis-1/12 p-4 hidden md:inline-block">
                <Sidebar isIdolslist={isIdolslist}/>
              </div>
              <div className="flex-auto md:p-4">
                 {children}
              </div>
            </div>
          </main>
          <Footer />
        </div>
      </CheckboxContext.Provider>

    )
  }

  export default Layout
