import React, { useState } from 'react';

type Props = {
    name : string
    isCheck?: boolean
    color: string
    onChange? : () => void
}


const Checkbox: React.FC<Props> = ( {name, isCheck, color, onChange}) => {

    const check = isCheck === null ? false : isCheck;

    const [isChecked, setIsChecked] = useState(check);

    return (
        <label className="flex my-1 items-center hover:bg-gray-200 px-2">
            <input
                type="checkbox"
                checked={isChecked}
                // onChange={() => setIsChecked(!isChecked)}
                onChange={() => {
                    setIsChecked(!isChecked);
                    onChange();
                  }}
                className= {`${color} w-4 h-4`}
            />
            <p className="px-2 truncate">{name}</p>
        </label>
    );

}

export default Checkbox;
