import { ReactNode } from 'react'

import Header from './header'
import Footer from './footer'

type Props = {
  children: ReactNode;
}

const Layout = ({ children }: Props) => {

    return (
      <div className="App flex flex-col">
          <Header />
          <main className="p-6">
           {children}
          </main>
          <Footer />
      </div>
    )
  }

  export default Layout
