import IdolsList from "./molecule/idolslist";

type Props = {
    isIdolslist?: boolean
}

const Sidebar: React.FC<Props> = ( {isIdolslist} ) =>{

    return (
        <div>
            {isIdolslist ? <IdolsList /> : null}
        </div>

    );
}

export default Sidebar;

