import React, { useState, useEffect, useContext } from 'react';
import FullCalendar from '@fullcalendar/react';
import dayGridPlugin from '@fullcalendar/daygrid';
import jaLocale from '@fullcalendar/core/locales/ja';
import listPlugin from '@fullcalendar/list';

import CheckboxContext from '../../contexts/CheckboxContext';

import "../../styles/calendar.sass"

// type Props = {
//   id: string
//   title: string
//   groupId: string
//   start: string
//   url: string
// }


const Calendar: React.FC = (): JSX.Element => {

    const [events, setEvents] = useState([]);

    // eslint-disable-next-line @typescript-eslint/no-unused-vars
    const { checkList, setCheckList, groupidList, setGroupidList } = useContext(CheckboxContext);
    console.log(checkList)
    console.log(groupidList)

    useEffect(() => {
        // APIからJSONデータを取得
        const fetchEvents = async () => {
          const res = await fetch('https://jbwg9oogwc.execute-api.ap-northeast-1.amazonaws.com/json', {
            method: 'POST',
            mode: "cors",
            headers: {
              'Content-Type': 'application/json',
            },
          });
          const data = await res.json();

          // console.log(data);
          setEvents(data);
        };
        fetchEvents();
      }, []);

      const result = events.filter((info: { groupId: string; }) => {
        return groupidList.includes(info.groupId);
      });

      console.log(result)


    return (
      <div>
        <FullCalendar
            plugins={[dayGridPlugin, listPlugin]}
            initialView="dayGridMonth"
            locales={[jaLocale]}
            locale="ja"
            height={"100vh"}
            contentHeight={"auto"}
            headerToolbar={
                { left: 'prev today next', center: 'title', right: 'dayGridMonth listYear'}
            }
            events={events}

            // eventのcssを調整
            eventDidMount={(info) => {
                const event = info.event;
                const element = info.el;

                element.style.borderColor = 'transparent';
                element.classList.add('text-xs', 'leading-5', 'tracking-wide', 'pl-1',);

                // group毎の色を決定
                if (event.groupId === '1') {
                    element.style.backgroundColor = '#f97316';
                  } else if (event.groupId === '2') {
                    element.style.backgroundColor = '#84cc16';
                }

                // hober時の動作
                element.addEventListener('mouseenter', () => {
                    element.style.opacity = '0.6';
                });
                element.addEventListener('mouseleave', () => {
                    element.style.opacity = '1';
                });
              }}

            // eventのクリック時の動作
            eventClick={info => {
                window.open(info.event.url, "_self");
                // window.open(info.event.url, "_blank");
              }}
        />

      </div>
    );
  }

  export default Calendar;
